mapper.insert:
insert into board(
	 b_no
	 , subject
	 , writer
	 , readcount
	 , content
	 , pwd
	 , email
	 , group_no
	 , print_no
	 , print_level
) values(
	(select nvl(max(b_no),0)+1 from board)
	 , #{subject} 
	 , #{writer} 
	 , 0
	 , #{content} 
	 , #{pwd} 
	 , #{email} 
	 , (select nvl(max(b_no),0)+1 from board)
	 , 0
	 , 0
)
