mapper.insert:
insert into board(
	 b_no
	 , subject
	 , writer
	 , readcount
	 , content
	 , pwd
	 , email
	 , group_no
	 , print_no
	 , print_level
) values(
	(select nvl(max(b_no),0)+1 from board)
	 , #{subject} 
	 , #{writer} 
	 , 0
	 , #{content} 
	 , #{pwd} 
	 , #{email} 
	 , (select nvl(max(b_no),0)+1 from board)
	 , 0
	 , 0
)

mapper.update:
update board 
set writer = #{writer}
	, subject = #{subject}
	, email = #{email}
	, content = #{content}
where b_no=#{b_no} and pwd=#{pwd}

mapper.delete:
delete from board
where b_no=#{b_no}
	and pwd=${pwd}
